tinyMCE.PluginManager.add("stylebuttons",function(t){["p","h1","h2","h3"].forEach(function(n){t.addButton("style-"+n,{tooltip:"Toggle "+n,text:n.toUpperCase(),onClick:function(){t.execCommand("mceToggleFormat",!1,n)},onPostRender:function(){var o=this,e=function(){t.formatter.formatChanged(n,function(t){o.active(t)})};t.formatter?e():t.on("init",e)}})})});
